INSERT INTO "towns" VALUES (67000210, 67000, '臺南市', '安定區', '本地原稱為『直加弄』，這是譯自當地西拉雅族目加溜灣社之屬社的社名。民國九年時，依據本地在清代時，屬於『安定東里』，便改稱為『安定』。');
INSERT INTO "towns" VALUES (67000220, 67000, '臺南市', '山上區', '山上舊稱為『山子頂』，係因本鄉屬於丘陵地形，境內分佈著許多的小山丘，聚落多建立在小山丘上，故稱為『山仔頂』，後來則改稱為同義的『山上』。');
INSERT INTO "towns" VALUES (67000230, 67000, '臺南市', '玉井區', '玉井在荷蘭占領及清代稱「大武瓏」，是曹族四社熟番－醮吧年社、芒子芒社、宵里社及今楠西鄉的茄拔社等原住民所住。民國九年才以「醮吧年」Tapani的日語近似音「玉井」（日語Tamai）來稱呼本鄉，而沿用至現在。（臺南縣鄉土教材，民84，臺南縣政府 出版）');
INSERT INTO "towns" VALUES (67000240, 67000, '臺南市', '楠西區', '本地舊稱為『茄拔』，這是出自阿里山四社群中『茄拔』社之社名，後來則改稱為『楠西』，這是因為本地在清代時是屬於『楠梓仙溪西里』，後來便將其簡寫成『楠西』，而成為本地之地名。');
INSERT INTO "towns" VALUES (67000250, 67000, '臺南市', '南化區', '本地舊稱為『南庄』，民國九年時，乃取南庄之『南』與其所隸屬『內新化南里』之『化』，拼成『南化』。而『南庄』則是相對於對岸的北寮，位於北寮南方之村落，即稱為『南庄』。');
INSERT INTO "towns" VALUES (67000260, 67000, '臺南市', '左鎮區', '『左鎮』地名之由來，是因為明鄭時期，設侍衙駐兵於轄內之左隅，故稱「左鎮」。（臺南縣鄉土教材，民84，臺南縣政府 出版）');
INSERT INTO "towns" VALUES (67000270, 67000, '臺南市', '仁德區', '仁德舊稱為『土庫』，係指以竹子編成並塗上泥土的庫倉，這種庫倉是用來儲藏穀子的，由於本地有許多土庫，故有此稱。民國九年時，改以明鄭時期本地所設之『仁德里』為名而改為『仁德』。');
INSERT INTO "towns" VALUES (67000280, 67000, '臺南市', '歸仁區', '本地舊稱紅瓦厝，乃是鄉民多以紅瓦覆屋頂而得名。明鄭時期，因鄭氏勸導歸化，並設歸仁里。光復後改為「歸仁鄉」。（臺南縣鄉土教材，民84，臺南縣政府 出版）');
INSERT INTO "towns" VALUES (67000290, 67000, '臺南市', '關廟區', '此地原名「香洋」，因種植香洋得名。後因境內關廟村20號之山西堂，奉祀關聖帝君，故命名為「關廟」（臺南縣鄉土教材，民84，臺南縣政府 出版）');
INSERT INTO "towns" VALUES (67000300, 67000, '臺南市', '龍崎區', '本鄉名稱由來是在民國九年時，從鄉內的龍船（龍船村）及崎頂（崎頂村）兩地名中，各取一字併成庄名，光復後沿用至今。');
INSERT INTO "towns" VALUES (67000310, 67000, '臺南市', '永康區', '永康舊稱為埔姜頭，因為以前此地未開墾時，為埔姜樹蔓生之荒埔，所以得名。明鄭成功時為部將林、黃等員駐屯之地，稱「永康里」。日據時地名大改革，乃以明鄭時期本地隸屬「永康里」，改稱「永康庄」。光復後改庄為鄉，民國八十一年改制為永康市。（臺南縣鄉土教材，民84，臺南縣政府 出版）');
INSERT INTO "towns" VALUES (67000320, 67000, '臺南市', '東區', '本區位於本市的東南方，在日治時期，本區純位於本市的東部，故命名為東區，後來於民國35年將安順庄（今安南區）併入本市後，使本區位置趨於東南，但仍命名為東區。');
INSERT INTO "towns" VALUES (67000330, 67000, '臺南市', '南區', '本區因位於本市的南端，故命名為南區。');
INSERT INTO "towns" VALUES (67000340, 67000, '臺南市', '北區', '本區位於台南府城的北邊，古廟甚多，極具歷史風味。');
INSERT INTO "towns" VALUES (67000350, 67000, '臺南市', '安南區', '本區原屬於台南縣之安順鄉，民國35年奉令劃歸台南市管轄，為紀念從台南縣安順鄉劃分歸入台南市，乃取安順鄉之「安」字及台南市之「南」字，合併命名本區為「安南區」。');
INSERT INTO "towns" VALUES (67000360, 67000, '臺南市', '安平區', '安平」一名之由來有兩種說法，一種說法是指鄭成功在登陸此地打敗荷蘭人之後，懷念其泉州府晉江縣安平鎮的故居，乃將此地的原名「一鯤身」改名為「安平」（花松村，1996）。另一種說法是指本地往昔為台灣南方的第一良港，船舶出入甚為平穩安定，故稱「安平」（安倍明義，1996）。');
INSERT INTO "towns" VALUES (67000370, 67000, '臺南市', '中西區', '本區位於本市之中央，故命名為中區。本區因位於本市的西邊，故命名為西區。');
INSERT INTO "towns" VALUES (68000010, 68000, '桃園市', '桃園區', '在清朝時，此地就有漢人入墾，當時在此地遍植桃樹，所以就稱此地為『桃園』。');
INSERT INTO "towns" VALUES (68000020, 68000, '桃園市', '中壢區', '本地以前正位於竹塹與八里坌中間，是旅客、商人往來必經的中途站，再加上此地多地塹地形，客家語稱此地塹地形為『壢』，所以稱此地為『中壢』。');
INSERT INTO "towns" VALUES (68000030, 68000, '桃園市', '大溪區', '大溪位於淡水河的幹流大漢溪旁，舊稱為『大嵙崁』，是來自於當地的凱達格蘭族霄裡社人稱大漢溪的譯音，後來才改稱為『大溪』。');
INSERT INTO "towns" VALUES (68000040, 68000, '桃園市', '楊梅區', '在清乾隆年間，此地即有漢人來此拓墾，當時墾民在此地遍植楊梅樹，故稱此地為『楊梅』。');
INSERT INTO "towns" VALUES (68000050, 68000, '桃園市', '蘆竹區', '本鄉在開墾初期，是一塊蘆竹茂生之地，移民在此地居住形成聚落，就稱此地為『蘆竹』。');
INSERT INTO "towns" VALUES (68000060, 68000, '桃園市', '大園區', '此地舊稱『大坵園』，意思是指大塊的旱田，因為以前此地缺乏水利，只能種植甘藷及一些耐旱的蔬菜，所以就稱為『大坵園』，後來才簡稱『大園』。');
INSERT INTO "towns" VALUES (68000070, 68000, '桃園市', '龜山區', '本鄉地名的由來是取自於以前境內的平埔族社名『龜崙社』，後來將『崙』改為『山』而形成今天的『龜山』。');
INSERT INTO "towns" VALUES (68000080, 68000, '桃園市', '八德區', '八德舊名為『八塊厝』，是指以前此地初建聚落時，只有八戶人家居住。台灣光復後，最初稱為八塊鄉，後來改稱八德鄉。');
INSERT INTO "towns" VALUES (68000090, 68000, '桃園市', '龍潭區', '此地名的由來是因境內有一大陂潭『龍潭陂』，故有此稱。');
INSERT INTO "towns" VALUES (68000100, 68000, '桃園市', '平鎮區', '此地舊稱為『安平鎮』，是因為以前為了保護從大湖口至中壢、桃園之行旅的安全，曾在此地設置了張望寮，所以原稱此地為『張望寮』，後來取以保護平安的意思，改稱為『平安鎮』。民國九年時，則簡稱為『平鎮』。');
INSERT INTO "towns" VALUES (68000110, 68000, '桃園市', '新屋區', '清乾隆年間，此地西邊附近，就有漢人來此開墾，由於常遭平埔族人的襲擊獵首，乃棄原來住屋向東移，另築新屋以居，『新屋』便成為此地的地名。');
INSERT INTO "towns" VALUES (68000120, 68000, '桃園市', '觀音區', '此地舊名為『石觀音』，是因為當地有位民眾在今天的觀音溪溪流中，發現一塊酷似佛像的天然石頭，大家都認為是觀音菩薩像，於是建廟來供奉，頗為靈驗。民國九年時，即依此取名為『觀音』。');
INSERT INTO "towns" VALUES (68000130, 68000, '桃園市', '復興區', '此地舊稱為『角板鄉』，是因為境內有角板山的緣故，而『角板山』名稱的由來，是因為以前劉銘傳至此開山撫番時，見此地山峰突起如角，而河階面平坦如板，所以就命此地為『角板山』。後來改稱此地為『復興』，則是有冀以民族復興的意思。');
